import { ArrowLeftOutlined } from '@ant-design/icons';
import {
  Button,
  Col,
  Divider,
  Drawer,
  Form,
  Image,
  Input,
  Row,
  Select,
  Space,
  Tooltip,
  Typography,
} from 'antd';
import React, { useState } from 'react';
import { editGameMetadata } from '.';
import Background from '../shared/Background/Background';
import UploadImage from '../shared/UploadImage/UploadImage';
import './GameEditorPage.scss';

const { Title, Text } = Typography;

const GameMetadataForm = ({ game }) => {
  const [isLoading, setIsLoading] = useState(false);
  const [isOpenSidebar, setIsOpenSidebar] = useState(true);
  const [form] = Form.useForm();
  const thumbnailPhotoUrlValue = Form.useWatch('thumbnail', form);
  const backgroundPhotoUrlValue = Form.useWatch('background', form);

  const handleThumbnailChange = (result) => {
    form.setFieldValue('thumbnail', result);
  };
  const handleBackgroundChange = (result) => {
    form.setFieldValue('background', result);
  };
  const onFinish = (values) => {
    const gameData = {
      ...values,
    };
    setIsLoading(true);
    editGameMetadata(game.id, gameData).finally(() => {
      setIsLoading(false);
    });
  };
  const onSave = () => {
    form.submit();
  };
  return (
    <div className="game_editor_form_container">
      <Space className="action right" direction="vertical">
        <Tooltip title="Metadata" placement="left">
          <Button
            type="primary"
            shape="circle"
            onClick={() => setIsOpenSidebar(true)}
            icon={<ArrowLeftOutlined />}
          />
        </Tooltip>
      </Space>
      <Form
        name="game-metadata"
        layout="vertical"
        initialValues={game}
        onFinish={onFinish}
        autoComplete="off"
        form={form}
        disabled={isLoading}
      >
        {/* <Form.Item
          name="content"
          rules={[{ required: true, message: 'Please input the content!' }]}
        >
          <PlayableContentForm></PlayableContentForm>
        </Form.Item> */}
        <Drawer
          placement="right"
          onClose={() => setIsOpenSidebar(false)}
          mask={false}
          open={isOpenSidebar}
          width={368}
          extra={
            <Space>
              <Button type="primary" onClick={onSave}>
                Save
              </Button>
            </Space>
          }
        >
          <Row className="playable_metadata_container">
            <Col span={24}>
              <div>
                <Title level={3}>Metadata</Title>
                <Text>
                  <Text type="danger">*</Text> Name
                </Text>
                <Form.Item
                  name="name"
                  rules={[
                    { required: true, message: 'Please input the name!' },
                    { max: 150, message: 'Maximum 150 characters' },
                  ]}
                >
                  <Input.TextArea
                    placeholder="Name"
                    autoSize={{ maxRows: 3 }}
                    maxLength={150}
                  />
                </Form.Item>

                <div className="group_form_item">
                  <UploadImage
                    onChange={handleThumbnailChange}
                    previewImageStyle={{
                      width: '20rem',
                      maxHeight: '12rem',
                      objectFit: 'cover',
                    }}
                    initialValue={thumbnailPhotoUrlValue}
                  >
                    <div className="thumbnail_image upload_cover">
                      {thumbnailPhotoUrlValue ? (
                        <Image
                          src={thumbnailPhotoUrlValue}
                          preview={false}
                          onError={({ currentTarget }) => {
                            currentTarget.onerror = null;
                            currentTarget.src =
                              'data:image/png;base64,';
                          }}
                        />
                      ) : (
                        <img
                          className="upload_empty_button"
                          alt="upload cover"
                          height={'12rem'}
                          src={
                            'data:image/png;base64,'
                          }
                        />
                      )}
                      <div className="upload_cover_hover">
                        <Text type="secondary">Upload thumbnail</Text>
                      </div>
                    </div>
                  </UploadImage>
                  <Form.Item
                    name="thumbnail"
                    rules={[
                      { type: 'url', warningOnly: true },
                      { required: true, message: 'Please input a photo URL!' },
                    ]}
                  >
                    <Input readOnly />
                  </Form.Item>
                </div>

                <div className="group_form_item">
                  <UploadImage
                    onChange={handleBackgroundChange}
                    previewImageStyle={{
                      width: '20rem',
                      maxHeight: '12rem',
                      objectFit: 'cover',
                    }}
                    initialValue={backgroundPhotoUrlValue}
                  >
                    <div className="background_image upload_cover">
                      {backgroundPhotoUrlValue ? (
                        <Background src={backgroundPhotoUrlValue}></Background>
                      ) : (
                        <img
                          className="upload_empty_button"
                          alt="upload cover"
                          height={'200px'}
                          src={
                            'data:image/png;base64,'
                          }
                        />
                      )}
                    </div>
                    <div className="upload_cover_hover">
                      <Text type="secondary">Upload background</Text>
                    </div>
                  </UploadImage>
                  <Form.Item
                    name="background"
                    rules={[
                      { type: 'url', warningOnly: true },
                      { required: true, message: 'Please input a photo URL!' },
                    ]}
                  >
                    <Input readOnly />
                  </Form.Item>
                </div>

                <Divider></Divider>
                <Text>
                  <Text type="danger">*</Text> Description
                </Text>
                <Form.Item name="description">
                  <Input.TextArea
                    autoSize={{ minRows: 2, maxRows: 6 }}
                    placeholder="Description"
                  />
                </Form.Item>
                <Form.Item name="status">
                  <Select
                    options={[
                      { value: 'Work in progress', label: 'Work in progress' },
                      { value: 'Published', label: 'Published' },
                    ]}
                  ></Select>
                </Form.Item>
              </div>
            </Col>
          </Row>
        </Drawer>
      </Form>
    </div>
  );
};

export default GameMetadataForm;
