import { Form, Image, Input, Modal, Radio } from 'antd';
import React, { useEffect, useState } from 'react';
import './UploadImage.scss';

const parseGoogleUrl = (url) => {
  const id = url?.split('/file/d/')[1]?.split('/view')[0] || '';
  return `https://drive.google.com/uc?export=view&id=${id}`;
};

const reverseGoogleUrl = (url) => {
  const id = url?.split('https://drive.google.com/uc?export=view&id=')[1] || '';
  return `https://drive.google.com/file/d/${id}/view`;
};

const UploadImage = (props) => {
  const { children, onChange, previewImageStyle, initialValue } = props;
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isError, setIsError] = useState(false);
  const [form] = Form.useForm();
  const imageUrlValue = Form.useWatch('imageUrl', form);
  const urlTypeValue = Form.useWatch('urlType', form);

  const [imagleUrl, setImageUrl] = useState('');

  useEffect(() => {
    if (isModalOpen) {
      if (initialValue?.match('drive.google.com')) {
        form.setFieldValue('urlType', 'google-drive');
        form.setFieldValue('imageUrl', reverseGoogleUrl(initialValue));
      }
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [initialValue, isModalOpen]);

  useEffect(() => {
    switch (urlTypeValue) {
      case 'google-drive':
        setImageUrl(parseGoogleUrl(imageUrlValue));
        break;
      default:
        setImageUrl(imageUrlValue);
        break;
    }
  }, [imageUrlValue, urlTypeValue]);

  const showModal = () => {
    setIsModalOpen(true);
  };

  const handleOk = () => {
    form.submit();
  };

  const handleCancel = () => {
    setIsModalOpen(false);
    form.resetFields();
  };

  const handleFinish = () => {
    if (!isError) {
      !!onChange && onChange(imagleUrl);
      setIsModalOpen(false);
    }
  };

  const handlerSuccess = () => {
    setIsError(false);
  };
  const handlerError = () => {
    setIsError(true);
  };

  return (
    <>
      <div className="button_container" onClick={showModal}>
        {children}
      </div>
      <Modal
        title="Image"
        open={isModalOpen}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <Form
          name="imageUrl"
          layout="vertical"
          form={form}
          onFinish={handleFinish}
        >
          <Form.Item name="urlType" initialValue={'normal'}>
            <Radio.Group buttonStyle="solid">
              <Radio.Button value="normal">Normal</Radio.Button>
              <Radio.Button value="google-drive">Google Drive</Radio.Button>
            </Radio.Group>
          </Form.Item>
          <Form.Item
            label="Image Url"
            name="imageUrl"
            rules={[
              { required: true, message: 'Please input image url' },
              { type: 'url', warningOnly: true },
            ]}
            initialValue={initialValue}
          >
            <Input placeholder="Paste a image url" />
          </Form.Item>
          <Image
            className="preview_image"
            src={imagleUrl || 'error'}
            style={previewImageStyle}
            onLoad={handlerSuccess}
            onError={handlerError}
            fallback="data:image/png;base64,"
          />
        </Form>
      </Modal>
    </>
  );
};

export default UploadImage;
